import { useState, useCallback } from 'react';

export default () => {
    const [id, setId] = useState('');
    const onChangeId = useCallback((e)=> {//컴포넌트에 props로 넘겨주는 함수는 useCallback을 꼭 써달라
        setId(e.target.value);
    }, []);
}
